q = mpz(0x01e27a9ffaa13dd55202d4d5efb4e3cb59aae9ab187850cceceb13ad00566875b8dc066b3ac8c8cf9f5af22d817bde5c903ad20142054cb3ea79fcafc8f78ca88faed1994d9f3337aa96eba637b1649707726323f3a21c721a14cdac265f65a98091643556cfc337831ddb13821e169e3b7e371df3727fdfa86a7f5bd559383b89)
p = mpz(0x0292d53b6051337c5cca38a873f4b1fd7ad6a868063e4809eff658bfaae93a3db98799ffb05047b730be5a41ec9f55886acb7b91c10e2a26a6b328e40058e0ad1b890e2e75108c07d2f1be55299118674d66ca5650f56958eb5d7d3ace8734a39e93fd8c6768fa5978ed1dd8acc405eae01aba8c8125650ff5e5dd2334bbe93d89)

